import React, { useEffect, useState } from "react";
import axios from 'axios'
import {Container, Button} from '../Styles/Styles'
import CharacterCard from './CharacterCard'



export const  CharacterList = () => {
  // TODO: Add useState to track data from useEffect
  const [characters, setCharacters] = useState([])
  const [totalPages, setTotalPages] = useState()
  const [currentPage, setCurrentPage] = useState(3) //I'm setting my page data here.

  // let pageNext = () => {
  //   if(currentPage >= 1 && currentPage <= page.length -1){
  //     setCurrentPage(currentPage++)
  // }
  //   console.log(currentPage)
  // }

//   let pagePrevious = () => {
//     if(characterPage > 1 && characterPage <= response.info.pages.length){
// 		setCharacterPage(characterPage--)
// }


  useEffect(() => {
    axios.get(`https://rickandmortyapi.com/api/character/?page=${currentPage}`)
    .then(response => {
      setCharacters(response.data.results)
      setTotalPages(response.data.info)
      // setCurrentPage(1)
    })
    .catch(error => {
      console.log(error)
    })
  }, [])

  // console.log(currentPage)
  console.log(characters);
  console.log(totalPages)

  return (
  <div>
    <Container><h1>Characters!</h1></Container>
    <Container>
      <Button>Previous
      </Button>

      {/* onClick={pageUp} */}

      <Button >Next
      </Button>
    </Container>

    <Container> {/*This is just the place where I'm getting to pass in value for my props.*/}
      {characters.map((item, index) => {//Doesn't run the map function until we get data back from the API.
        return (
        <CharacterCard 
          item = {item}
          key = {index} />
          )})}
    </Container>
    </div>
  );
}
