import React, { useEffect, useState } from "react";
import axios from 'axios'
import styled from 'styled-components'

const CardContainer = styled.div `
    margin: 0 auto;
    width: 80%;
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
`

const ListHeading = styled.ul`

    list-style-type: none;
    justify-content: center;
    text-align: center;
    margin: 0 auto;
    margin: 0 auto;
    border: 0;
    padding: 0;
`

const List = styled.p `
    list-style-type: none;
    justify-content: center;
    text-align: center;
    margin: 0 auto;
    border: 0;
    padding: 0;
    color: white;
    font-size: 1.2rem;
    line-height: 1.5rem;

`

export const EpisodeList = (props) =>  {
  // TODO: Add useState to track data from useEffect
  const [episodes, setEpisodes] = useState([false])//gives us a blank array for putting data into.

  //Episodes
  useEffect(() => {
    axios.get('https://rickandmortyapi.com/api/episode')
    .then(response => {
      // console.log(response.data.results)
      setEpisodes(response.data.results)
    })
    .catch(err => {
      console.log(err)
    })
  }, [])

console.log(episodes)
  return (
      <CardContainer> {
        <>
        <h2>Name: {episodes.name}</h2>
        <h4>Episode: {episodes.episode}</h4>

        <ListHeading>
                    <List>Date of birth: {person.birth_year}</List>
                    <List>Gender: {person.gender === 'n/a' ? "No gender" : person.gender }</List>
                    <List>Eye Color: {person.eye_color === 'n/a' ? "No eye color" : person.eye_color }</List>
                    <List>Hair Color: {person.hair_color === 'n/a' ? "No hair color" : person.hair_color }</List>
                    <List>Height: {person.height}cm</List>
                    <List>Mass: {person.mass}kg</List>
                </ListHeading>




        </>
      }
      </CardContainer>
  );
}
